<?phpnamespace Gravel;class Controller{	public function __construct()	{	}	public function view($data)	{	}	public function load($file, $data = [])	{		$file = preg_replace('!\.php$!', '', $file) . '.php';		if (file_exists(APP_DIR . "/views/{$file}")) {			Gravel::$response->addOutput(APP_DIR . "/views/{$file}", $data);		}	}}