<?phpnamespace Gravel\Core;use Gravel\Core\TemplateEngine;class Response{	public $data = [		'views'    => [],		'compiled' => ''	];	public function __destruct()	{		$this->generateResponse();	}	public function generateResponse()	{		TemplateEngine::compile();		echo TemplateEngine::$data['compiled'];	}	public function addOutput($file, $data = [])	{		TemplateEngine::parseTemplate($file, $data);	}}