<?phpnamespace Gravel\Core;class Response{	public $data = [];	public function __destruct()	{		$this->generateResponse();	}	public function generateResponse()	{		header("Content-Type: text/plain");		print_r($this->data);	}	public function addOutput($file, $data = [])	{		ob_start();		require($file);		$output = ob_get_clean();		$file = substr($file, strlen(APP_DIR . '/views/'));		if (!preg_match("!@(extends|section|endsection)!", $output)) {			$this->data[$file] = $output;		} else {		}	}}