<?phpnamespace Gravel\Core;use Gravel\Gravel;use Gravel\TemplateEngine;class Response{	public $data = [		'views'    => [],		'compiled' => ''	];	public function __destruct()	{		$this->generateResponse();	}	public function generateResponse()	{		TemplateEngine::compile();		if (Gravel::$config['gravel']['tidy_html'] && class_exists('Tidy')) {			$html = new \Tidy();			$config = [				'indent'        => 1,				'indent-spaces' => 4,				'output-xhtml'  => 'false',				'wrap'          => 0			];			$html->parseString(TemplateEngine::$data['compiled'], $config);		}		else {			$html = TemplateEngine::$data['compiled'];		}		echo $html;		echo "\n\n<!-- generated in " . number_format(microtime(true) - \Gravel\Gravel::$startTime, 5) . " seconds -->";	}	public function addOutput($file, $data = [])	{		TemplateEngine::parseTemplate($file, $data);	}}